select `alldetailed`.`export` AS `export`,`alldetailed`.`PTR` AS `PTR`,`alldetailed`.`nolonSupport` AS `nolonSupport`,`alldetailed`.`manifestoSupport` AS `ManifestoSupport`,((`alldetailed`.`PTR` + `alldetailed`.`nolonSupport`) + `alldetailed`.`manifestoSupport`) AS `total`,`alldetailed`.`manualWork` AS `manualWork`,`alldetailed`.`company` AS `company` from `uniexport`.`alldetailed` where ((`alldetailed`.`boles` = 1) and (`alldetailed`.`manualWork` = 1) and (not(`alldetailed`.`id` in (select `uniexport`.`filenodata`.`exportCertificate` from `uniexport`.`filenodata`))) and `alldetailed`.`id` in (select `uniexport`.`bankreceiptdata`.`exportCertificate` from `uniexport`.`bankreceiptdata`)) union select 'Total' AS `Total`,sum(`alldetailed`.`PTR`) AS `sum(PTR)`,sum(`alldetailed`.`nolonSupport`) AS `Sum(nolonSupport)`,sum(`alldetailed`.`manifestoSupport`) AS `sum(ManifestoSupport)`,sum(((`alldetailed`.`PTR` + `alldetailed`.`nolonSupport`) + `alldetailed`.`manifestoSupport`)) AS `sum(PTR+nolonSupport+ManifestoSupport)`,' ' AS `Name_exp_13`,`alldetailed`.`company` AS `company` from `uniexport`.`alldetailed` where ((`alldetailed`.`boles` = 1) and (`alldetailed`.`manualWork` = 1) and (not(`alldetailed`.`id` in (select `uniexport`.`filenodata`.`exportCertificate` from `uniexport`.`filenodata`))) and `alldetailed`.`id` in (select `uniexport`.`bankreceiptdata`.`exportCertificate` from `uniexport`.`bankreceiptdata`)) group by `alldetailed`.`company`